import React, { Fragment, useState } from "react";

import "./MainSection.css";

import wildfire from "../../assets/wildfire1.jpg";
import flood from "../../assets/flood.jpg";
import NavigationBar from "../../components/NavigationBar/NavigationBar";

const images = [wildfire, flood];

const MainSection = () => {
  const [imgIndex, setImgIndex] = useState(0);

  const changeImage = () => {
    setImgIndex(imgIndex + (1 % images.length));
  };

  setTimeout(changeImage, 1000);

  return (
    <Fragment>
      <div
        className="bg"
        style={{
          backgroundImage: `url(${images[imgIndex]})`,
        }}
      >
        <div id="main-section">
          <NavigationBar />
          <div className="center-text">
            <span className="allert">ALLERT</span>
            <small className="slog">forewarned is forearmed</small>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default MainSection;
